// Calculate spend recommendation based on weather
function calculateSpendRecommendation(data) {
  const temp = data.main.temp;
  const condition = data.weather[0].main;
  const humidity = data.main.humidity;

  let change = 0;
  let reason = 'Normal weather conditions - maintain current spend';
  let confidence = 75;

  // Temperature-based recommendations
  if (temp > 90) {
    change = 30;
    reason = 'Extreme heat drives high demand for cooling products and indoor activities';
    confidence = 95;
  } else if (temp > 80) {
    change = 20;
    reason = 'Hot weather increases engagement for summer products and outdoor activities';
    confidence = 92;
  } else if (temp > 70) {
    change = 10;
    reason = 'Warm weather boosts outdoor shopping and lifestyle product interest';
    confidence = 87;
  } else if (temp < 35) {
    change = -20;
    reason = 'Very cold weather reduces foot traffic - focus on indoor/comfort products';
    confidence = 89;
  } else if (temp < 50) {
    change = -10;
    reason = 'Cold weather may reduce outdoor activities and shopping';
    confidence = 84;
  }

  // Weather condition adjustments
  if (condition === 'Rain' || condition === 'Drizzle') {
    change -= 8;
    reason = 'Rainy weather - shift budget to indoor activities, delivery services, and comfort products';
    confidence = 91;
  } else if (condition === 'Thunderstorm') {
    change -= 15;
    reason = 'Severe weather - reduce outdoor advertising, focus on essential products';
    confidence = 93;
  } else if (condition === 'Clear' && temp > 65 && temp < 85) {
    change += 8;
    reason = 'Perfect weather conditions - ideal for outdoor product campaigns and lifestyle marketing';
    confidence = 96;
  } else if (condition === 'Snow') {
    change -= 12;
    reason = 'Snow conditions - promote winter products, indoor entertainment, and delivery services';
    confidence = 88;
  }

  // Humidity adjustments for certain conditions
  if (humidity > 80 && temp > 75) {
    change += 5;
    reason += ' (High humidity increases demand for cooling solutions)';
  }

  // Ensure reasonable bounds
  change = Math.max(-40, Math.min(40, change));

  return { change, reason, confidence };
}

